(80, 1, '2020-05-17 15:22:23', '2020-05-17 07:22:23', '.TITLE{\n    font-size: 30px;\n}\n\n.picture_frame{\n    width: inherit;\n    height: inherit;\n}\n\n.Bob{\n    position: relative;\n}\n\n.Alice{\n    /*-transition: .5s ease;*/\n    /*opacity: 0;*/\n    position: absolute;\n    top: 50%;\n    left: 50%;\n    transform: translate(-50%, -50%);\n    /*-*/-ms-transform: translate(-50%, -50%);\n    /*display: flex;*/\n    /*似乎height即可*/min-height: 100%;\n    /*min-width: 100%;*/\n    /*mbackground-color:#4CAF50;*/\n    /*justify-content: center;*/\n    /*align-items: center;*/\n    z-index: 2;\n}\n\n.Bob:hover .box_picture {\n    opacity: 0.8;\n  }\n  \n.Bob:hover .Alice {\n    /*opacity: 1;*/\n  }\n\n#id_container_picture{\n    background-color: black;\n}\n\n  /*layer是指圖層，下層是底圖(picture)，上層是點選區塊(parts)*/\n.layer_picture{\n    /*position:initial;\n    position: absolute;*/\n    z-index: 1;\n}\n\n.layer_parts{\n    /*position:initial;\n    position: absolute;\n    z-index: 1;*/\n    /*width: inherit;\n    height: inherit;*/\n    z-index: 3;\n}\n\n/*Grid排版容器*/\n.container {\n    max-width: 800px;\n    /*-80*/width: 100%;/*控制grid Frame寬度*/\n    min-width:601px;\n    margin:0 0;/*auto會使頁寬大於content時，多的部分歸至margin*/\n    border: 1px solid blue;\n}\n\n    /*設定grid網格行列數*/\n    .grid {\n        display: grid;\n        /*grid-template-rows: 200px minmax(100px, max-content);*/\n        /*grid-template-rows: 200px repeat(2,minmax(100px, max-content));*/\n        /*grid-template-rows: repeat(2, 200px);*/\n        grid-template-rows: repeat(4, 1fr);/*fr means \'fraction\'*/\n        grid-template-columns: repeat(4, 1fr); \n        grid-gap:0;\n        grid-template-areas:\n        \"c c c c\"\n        \"c c c c\"\n        \"c c c c\"\n        \"c c c c\"\n        /*grid-template-columns:minmax(300px,1fr) 1fr 1fr 1fr;*//*RWD應用*/\n    }\n\n    /*--------以下為grid網格內的區塊顯示---------*/\n    .grid>.box_picture{\n        grid-area: c;\n        /*grid-row: 1 / 3;\n        grid-column: 1 / 3;*