（\\）*/\r\n    $data = htmlspecialchars($data);/*轉換HTML標籤語法*/\r\n  }\r\n\r\n	return $data;\r\n }\r\n\r\n/*用於表單性別*/\r\nfunction table_gender($data){\r\n  $word=\"\";\r\n  switch($data){\r\n    case \'Male\':\r\n      $word = \'男\';\r\n      break;\r\n    case \'Female\':\r\n      $word = \'女\';\r\n      break;\r\n    case \'Other\':\r\n      $word = \'其他\';  \r\n      break;\r\n    case \'Secret\':\r\n      $word = \'不公開\';\r\n      break;\r\n    default:\r\n      $word = \'存取錯誤\';\r\n  }\r\n  return $word;\r\n}\r\n\r\n/*用於表單提醒方式*/\r\nfunction table_note($datas){\r\n  $word=\"\";\r\n  foreach($datas as $onedata)\r\n  switch($onedata){\r\n    case \'email\':\r\n      $word = $word.\'Email \';\r\n      break;\r\n    case \'LINE\':\r\n      $word = $word.\'LINE \';\r\n      break;\r\n    case \'phonemessage\':\r\n      $word = $word.\'手機簡訊 \';  \r\n      break;\r\n    default:\r\n      $word = \'存取錯誤\';\r\n  }\r\n  return $word;\r\n}\r\n\r\nfunction K_testing(){\r\n    $test_var=100;\r\n    echo \"Double quotes: $test_var<br>\";\r\n    echo \'Apostrophe: $test_var\';\r\n}\r\n\r\n/*測試mysql+php server, 成功會回傳資料庫中流水號為1的位置資料*/\r\nfunction DB_testing(){\r\n  $data=0;\r\n  /*--$sql是SQL操作指令--*/\r\n  $sql = \"SELECT `POS` FROM `booking_info` WHERE SER = \'1\'\";\r\n  /*--mysqli_query()執行SQL操作，select指令會回傳SQL分類結果陣列，其他指令回傳True--*/\r\n  $rst =@mysqli_query($_SESSION[\'con\'], $sql);\r\n  if($rst){\r\n    $data=mysqli_fetch_assoc($rst);/*讀取陣列資料：mysqli_fetch_assoc($rst)是以欄位名稱作為索引標籤，並回傳矩陣*/\r\n    print_r($rst);\r\n    echo \'<br>\';\r\n    print_r($data);\r\n  }else{echo \"{$sql} comes out error\".mysqli_error($_SESSION[\'con\']);}\r\n  mysqli_free_result($rst);\r\n  return $data[\'POS\'];\r\n}', 'My php library', '', 'trash', 'closed', 'closed', '', 'my-php-library-2__trashed', '', '', '2020-05-17 22:57:00', '2020-05-17 14:57:00', '', 0, 'http://127.0.0.1/?post_type=wbcr-snippets&#038;p=129', 0, 'wbcr-snippets', '', 0),
