(175, 1, '2020-05-18 01:00:24', '2020-05-17 17:00:24', '/*首頁選區*/\r\n.picture_frame{\r\n    width: inherit;\r\n    height: inherit;\r\n}\r\n\r\n.Bob{\r\n    position: relative;\r\n}\r\n\r\n.Alice{\r\n    position: absolute;\r\n    top: 50%;\r\n    left: 50%;\r\n    transform: translate(-50%, -50%);\r\n    -ms-transform: translate(-50%, -50%);\r\n    min-height: 100%;\r\n    z-index: 2;\r\n}\r\n\r\n.Bob:hover .box_picture {\r\n    opacity: 0.8;\r\n  }\r\n  \r\n.Bob:hover .Alice {\r\n    /*opacity: 1;*/\r\n  }\r\n\r\n#id_container_picture{\r\n    background-color: black;\r\n}\r\n\r\n  /*layer是指圖層，下層是底圖(picture)，上層是點選區塊(parts)*/\r\n.layer_picture{\r\n    z-index: 1;\r\n}\r\n\r\n.layer_parts{\r\n    z-index: 3;\r\n}\r\n\r\n/*Grid排版容器*/\r\n.container {\r\n    /* max-width: 800px;*/\r\n    /* width: 100%;控制grid Frame寬度 */\r\n    /* min-width:601px; */\r\n    /* margin:0 0;auto會使頁寬大於content時，多的部分歸至margin */\r\n    border: 1px solid blue;\r\n}\r\n\r\n    /*設定grid網格行列數*/\r\n    .grid {\r\n        display: grid;\r\n        grid-template-rows: repeat(4, 1fr);/*fr means \'fraction\'*/\r\n        grid-template-columns: repeat(4, 1fr); \r\n        grid-gap:0;\r\n        grid-template-areas:\r\n        \"c c c c\"\r\n        \"c c c c\"\r\n        \"c c c c\"\r\n        \"c c c c\"\r\n    }\r\n\r\n    /*--------以下為grid網格內的區塊顯示---------*/\r\n    .grid>.box_picture{\r\n        grid-area: c;\r\n        max-width: 100%;\r\n        width: 100%;\r\n        height: auto;\r\n        backface-visibility: hidden;\r\n    }\r\n    \r\n    .grid>.box_parts {\r\n        /*父層grid二維排版；子層文字用Flexbox一維排版*/\r\n        border: 1px solid white;\r\n        color: white;\r\n        font-size: 30px;\r\n        display: flex;\r\n        justify-content: center;\r\n        align-items: center;\r\n        cursor: pointer;\r\n    }\r\n\r\n    .box_parts:hover{\r\n        background:rgba(0,255,0,50%);\r\n    }\r\n\r\n.HIDE{\r\n    display: none;\r\n}\r\n', 'homepage_css', '', 'inherit', 'closed', 'closed', '', 