(210, 1, '2020-05-18 11:47:13', '2020-05-18 03:47:13', '/*當格子被點選後，連結至報名表單頁*/\r\nfunction LinkTo_BookingPage(pos) {\r\n    /*抓取Protocol與Host*/\r\n    var GetProtocol = window.location.protocol;\r\n    var GetHost = window.location.host;\r\n    /*此連結使用HTTP GET require傳送參數，請保留?pos=\'+pos*/\r\n    window.location.href = GetProtocol+GetHost+\'/%e5%8a%83%e4%bd%8d%e8%b3%87%e6%96%99%e8%a1%a8%e5%96%ae/?pos=\'+pos;\r\n}\r\n\r\n/*依據DB回傳之位置狀態的不同，給予不同的顯示調整*/\r\nfunction show_state(x, status, pos){\r\n    switch (status) {\r\n        case \'1\':\r\n            x.innerHTML = \'<center>\' + pos + \':空位</center>\';\r\n            break;\r\n        case \'-1\':\r\n            x.innerHTML = \'<center>\' + pos + \':已被劃記</center>\';\r\n            break;\r\n        case \'0\':\r\n            x.innerHTML = \'<center>\' + pos + \':被報名中</center>\';\r\n            break;\r\n        default:\r\n            x.innerHTML = \'<center>\' + pos + \'<br>Something wrong!</center>\';\r\n    }\r\n}\r\n\r\n/*當滑鼠離開格子之後，讓顯示恢復原設定*/\r\nfunction set_origin(x, pos){\r\n    x.innerHTML=pos;\r\n}\r\n\r\n\r\n', 'ChoosePage.js', '', 'publish', 'closed', 'closed', '', 'choose_js', '', '', '2020-05-31 14:37:16', '2020-05-31 06:37:16', '', 0, 'http://127.0.0.1/?post_type=wbcr-snippets&#038;p=210', 0, 'wbcr-snippets', '', 0),
(211, 1, '2020-05-18 11:47:13', '2020-05-18 03:47:13', '\r\n// var zone_js = location.search;\r\n\r\n// 根據ID插入html\r\n// function insert_div(){\r\n//     var insertDiv = document.getElementById(\"id\");\r\n//     insertDiv.innerHTML = \"\";\r\n// }\r\n\r\nfunction LinkTo_BookingPage(pos) {\r\n    window.location.href = \'BookingPage.php?pos=\'+pos;\r\n}\r\n\r\nfunction show_state(x, status, pos){\r\n    switch(status){\r\n        case \'0\':\r\n            x.innerHTML=\'<center>\'+pos+\':空位<br>可以選取</center>\';\r\n            break;\r\n        case \'1\':\r\n            x.innerHTML=\'<center>\'+pos+\':已被劃記<br>請考慮其他的位置</center>\';\r\n            break;\r\n        case \'-1\':\r\n            x.innerHTML=\'<center>\'+pos+\':正被選取<br>請考慮其他的位置</center>\';\r\n            break;\r\n        default:\r\n            x.innerHTML=\'<center>\'+pos+\'<br>Something wrong!</center>\';\r\n    }\r\n\r\n}\r\n\r\nfunction set_origin(x, pos){\r\n    x.innerHTML=pos;\r\n}\r\n\r\n', 'Choose_js', '', 'inherit', 'closed', 'closed', '', '210-revision-v1', '', '', '2020-05-18 11:47:13', '2020-05-18 03:47:13', '', 210, 'http://127.0.0.1/2020/05/18/210-revision-v1/', 0, 'revision', '', 0),
(212, 1, '2020-05-18 11:48:14', '2020-05-18 03:48:14', '/*整個物件的最外層容器*/\r\n.canvas_container{\r\n    /*位置設為relative可被子層的absolute屬性區塊定位*/\r\n    position: relative;\r\n    /*自定義的RWD設定*/\r\n    /* max-width: 800px;\r\n    min-width:601px; */\r\n    /*依wordpress模板設定*/\r\n    width: 100%;\r\n    /*畫框邊線*/\r\n    border: 1px solid black;\r\n}\r\n\r\n/*圖片的屬性設定*/\r\n.img_Property{\r\n    /*<img>做到RWD縮放的必要設定*/\r\n    width: 100%;\r\n    height: auto;\r\n}\r\n\r\n/*設定Grid格線框架*/\r\n.grid_container{\r\n    /*位置設為absolute，便會將上層區塊的左上角作為參考點，作出相對位置的改變*/\r\n    position: absolute;\r\n    top: 0;\r\n    left: 0;\r\n    /*寬度設100%永遠填滿這個框架\r\n    \"最小高度100%\"是本物件會成功的重點，由於位置設為absolute，它會參考父層當前實際的高度作為自己的最低高度*/\r\n    width: 100%;\r\n    min-height: 100%;\r\n    /*將layout模組設為grid(格線框架)：會自動將子層區塊照自定義設定值排列*/\r\n    display: grid;\r\n    /*目前排列規則設為：4列，1fr代表全部的分割比例皆為1:1，W3Cschool會有更多這部分的變形教學*/\r\n    grid-template-rows: repeat(4, 1fr);\r\n    /*目前排列規則設為：4行*/\r\n    grid-template-columns: repeat(4, 1fr); \r\n    /*間隔：目前設為0，表示無間隔*/\r\n    grid-gap:0;\r\n}\r\n\r\n/*設定grid框架內的實際的方格*/\r\n.grid_item{\r\n    /*flex是目前最優的文字排版設定，適合用在最底層*/\r\n    display: flex;\r\n    /*文字區塊xy軸都置中排列*/\r\n    justify-content: center;\r\n    align-items: center;\r\n    /*文字屬性*/\r\n    border: 1px solid white;\r\n    /*格線顏色*/\r\n    color: white;\r\n    font-size: 5vw;\r\n}\r\n\r\n/*hover表示滑鼠移置此區塊上方之後的狀態*/\r\n.grid_item:hover{\r\n    /*滑鼠呈現手指圖案*/\r\n    cursor: pointer;\r\n    /*方格顏色轉變的顏色*/\r\n    background:rgba(0,255,0,50%);\r\n    font-size: 4vw;\r\n}\r\n\r\n.lock {\r\n    background: rgba(255, 0, 0, 50%);\r\n}\r\n\r\n.lock:hover {\r\n    background: rgba(255, 0, 0, 50%);\r\n    font-size: 100%;\r\n    cursor: default;\r\n    font-size: 3vw;\r\n}\r\n\r\n/*RWD響應式設定: 配合WP的Elwmentor*/\r\n/*手機螢幕*/\r\n/* @media (max-width: 360px) {\r\n    .grid_item{\r\n        font-size: 1em;\r\n    }\r\n} */\r\n/*平板螢幕*/\r\n/* @media (min-width: 361px) and (max-width: 768px) {\r\n    .grid_item{\r\n        font-size: 2em;\r\n    }\r\n} */\r\n/*電腦螢幕*/\r\n/* @media (min-width: 769px) and (max-width: 899px) {\r\n    .grid_item{\r\n        font-size: 2em;\r\n    }\r\n} */\r\n/*更大的電腦螢幕*/\r\n@media (min-width: 1000px) {\r\n    .grid_item{\r\n        font-size: 30px;\r\n    }\r\n	.grid_item:hover{\r\n		font-size: 30px;\r\n	}\r\n}', 'choosepage.css', '', 'publish', 'closed', 'closed', '', 'choosepage_css', '', '', '2020-05-31 14:37:04', '2020-05-31 06:37:04', '', 0, 'http://127.0.0.1/?post_type=wbcr-snippets&#038;p=212', 0, 'wbcr-snippets', '', 0);
