(321, 1, '2020-05-21 22:03:48', '2020-05-21 14:03:48', '/*當格子被點選後，連結至選擇位置頁(注意若更改頁面名稱後，此網址也要更換)*/\r\nfunction LinkTo_ChooingPage(zone){\r\n    /*此連結使用HTTP GET require傳送參數，請保留?zone=\'+zone;*/\r\n    window.location.href = \'http://127.0.0.1/%e6%84%9f%e6%81%a9%e7%89%86%e9%81%b8%e6%93%87%e4%bd%8d%e7%bd%ae%e9%a0%81/?zone=\'+zone;\r\n    /*此處也可直接用document.getElementById(\"id\").innerHTML=\"\";取得zone，降低程式碼複雜度*/\r\n    /*若活用this可更簡化*/\r\n}\r\n\r\n/*顯示目前格子代表的區域內還有多少空位*/\r\nfunction get_emptyseats(x, zone, number_Emptyseat){\r\n    x.innerHTML=\'<center>第\'+zone+\'區<br>還有\'+number_Emptyseat+\'空位</center>\';\r\n}\r\n\r\n/*當滑鼠離開格子之後，讓顯示恢復原設定*/\r\nfunction set_origin(x, zone){\r\n    x.innerHTML=\'第\'+zone+\'區\';\r\n}\r\n\r\n/*(捨棄)因為WP的防護機制，只好用php來寫\r\n產生欲嵌入id=\"id_grid_container\"的可點選方格*/\r\n// function grid_item_generate() {\r\n//     var i, s = \'\';\r\n//     /*i可控制要顯示多少個格子*/\r\n//     for (i = 0; i < 16; i++) {\r\n//         s += \"<div class=\\\"grid_item\\\">\" + i + \"</div>\";\r\n//     }\r\n//     document.getElementById(\'id_grid_container\').innerHTML = s;\r\n// }\r\n// grid_item_generate();', 'Homepage_Javascript', '', 'inherit', 'closed', 'closed', '', '111-revision-v1', '', '', '2020-05-21 22:03:48', '2020-05-21 14:03:48', '', 111, 'http://127.0.0.1/2020/05/21/111-revision-v1/', 0, 'revision', '', 0),
(322, 1, '2020-05-21 22:06:24', '2020-05-21 14:06:24', '/*當格子被點選後，連結至報名表單頁(注意若更改頁面名稱後，此網址也要更換)*/\r\nfunction LinkTo_BookingPage(pos) {\r\n    /*此連結使用HTTP GET require傳送參數，請保留?pos=\'+pos*/\r\n    window.location.href = \'BookingPage.php?pos=\'+pos;\r\n}\r\n\r\n/*依據DB回傳之位置狀態的不同，給予不同的顯示調整*/\r\nfunction show_state(x, status, pos){\r\n    switch(status){\r\n        case \'0\':\r\n            x.innerHTML=\'<center>\'+pos+\':空位<br>可以選取</center>\';\r\n            break;\r\n        case \'1\':\r\n            x.innerHTML=\'<center>\'+pos+\':已被劃記<br>請考慮其他的位置</center>\';\r\n            break;\r\n        case \'-1\':\r\n            x.innerHTML=\'<center>\'+pos+\':正被選取<br>請考慮其他的位置</center>\';\r\n            break;\r\n        default:\r\n            x.innerHTML=\'<center>\'+pos+\'<br>Something wrong!</center>\';\r\n    }\r\n\r\n}\r\n\r\n/*當滑鼠離開格子之後，讓顯示恢復原設定*/\r\nfunction set_origin(x, pos){\r\n    x.innerHTML=pos;\r\n}\r\n', 'ChoosePage_js', '', 'inherit', 'closed', 'closed', '', '210-revision-v1', '', '', '2020-05-21 22:06:24', '2020-05-21 14:06:24', '', 210, 'http://127.0.0.1/2020/05/21/210-revision-v1/', 0, 'revision', '', 0),
(323, 1, '2020-05-21 22:07:21', '2020-05-21 14:07:21', '/*整個物件的最外層容器*/\r\n.canvas_container{\r\n    /*位置設為relative可被子層的absolute屬性區塊定位*/\r\n    position: relative;\r\n    /*自定義的RWD設定*/\r\n    /* max-width: 800px;\r\n    min-width:601px; */\r\n    /*依wordpress模板設定*/\r\n    width: 100%;\r\n    /*畫框邊線*/\r\n    border: 1px solid black;\r\n}\r\n\r\n/*圖片的屬性設定*/\r\n.img_Property{\r\n    /*<img>做到RWD縮放的必要設定*/\r\n    width: 100%;\r\n    height: auto;\r\n}\r\n\r\n/*設定Grid格線框架*/\r\n.grid_container{\r\n    /*位置設為absolute，便會將上層區塊的左上角作為參考點，作出相對位置的改變*/\r\n    position: absolute;\r\n    top: 0;\r\n    left: 0;\r\n    /*寬度設100%永遠填滿這個框架\r\n    \"最小高度100%\"是本物件會成功的重點，由於位置設為absolute，它會參考父層當前實際的高度作為自己的最低高度*/\r\n    width: 100%;\r\n    min-height: 100%;\r\n    /*將layout模組設為grid(格線框架)：會自動將子層區塊照自定義設定值排列*/\r\n    display: grid;\r\n    /*目前排列規則設為：4列，1fr代表全部的分割比例皆為1:1，W3Cschool會有更多這部分的變形教學*/\r\n    grid-template-rows: repeat(4, 1fr);\r\n    /*目前排列規則設為：4行*/\r\n    grid-template-columns: repeat(4, 1fr); \r\n    /*間隔：目前設為0，表示無間隔*/\r\n    grid-gap:0;\r\n}\r\n\r\n/*設定grid框架內的實際的方格*/\r\n.grid_item{\r\n    /*flex是目前最優的文字排版設定，適合用在最底層*/\r\n    display: flex;\r\n    /*文字區塊xy軸都置中排列*/\r\n    justify-content: center;\r\n    align-items: center;\r\n    /*文字屬性*/\r\n    border: 1px solid white;\r\n    /*格線顏色*/\r\n    color: white;\r\n}\r\n\r\n/*hover表示滑鼠移置此區塊上方之後的狀態*/\r\n.grid_item:hover{\r\n    /*滑鼠呈現手指圖案*/\r\n    cursor: pointer;\r\n    /*方格顏色轉變的顏色*/\r\n    background:rgba(0,255,0,50%);\r\n}\r\n\r\n.lock {\r\n    background: rgba(255, 0, 0, 50%);\r\n}\r\n\r\n.lock:hover {\r\n    background: rgba(255, 0, 0, 50%);\r\n    font-size: 100%;\r\n    cursor: default;\r\n}\r\n\r\n/*RWD響應式設定: 配合WP的Elwmentor*/\r\n/*手機螢幕*/\r\n@media (max-width: 360px) {\r\n    .grid_item{\r\n        font-size: 1em;\r\n    }\r\n}\r\n/*平板螢幕*/\r\n@media (min-width: 361px) and (max-width: 768px) {\r\n    .grid_item{\r\n        font-size: 2em;\r\n    }\r\n}\r\n/*電腦螢幕*/\r\n@media (min-width: 769px) and (max-width: 899px) {\r\n    .grid_item{\r\n        font-size: 2em;\r\n    }\r\n}\r\n/*更大的電腦螢幕*/\r\n@media (min-width: 900px) {\r\n    .grid_item{\r\n        font-size: 2em;\r\n    }\r\n}\r\n', 'ChoosePage_css', '', 'inherit', 'closed', 'closed', '', '212-revision-v1', '', '', '2020-05-21 22:07:21', '2020-05-21 14:07:21', '', 212, 'http://127.0.0.1/2020/05/21/212-revision-v1/', 0, 'revision', '', 0),
