(421, 1, '2020-05-23 01:23:28', '2020-05-22 17:23:28', '/*由於表單外掛的設計，這個Javascript必須要引入在footer底下，和其他的snippets用法不同*/\r\n\r\n/*取得HTTP Get值*/\r\nfunction httpGet(){\r\n    /*利用location.href取得網址，並存入變數*/\r\n    var getUrlString = location.href;\r\n    /*將字串轉成URL*/\r\n    var url = new URL(getUrlString);\r\n    /*使用URL.searchParams + get 取得HTTP Get值*/\r\n    return String(url.searchParams.get(\'pos\'));\r\n\r\n}\r\n\r\n/*將副標題的位置資訊改成本頁面應顯示的的位置*/\r\ndocument.getElementById(\"id_FormPage_pos_tittle\").innerHTML = httpGet();\r\n\r\n/*將特定欄位給予預設值並加以鎖定*/\r\n/*src: https://www.w3schools.com/jsref/dom_obj_text.asp*/\r\nfunction AutoInsert_Lock_Field(){\r\n    var x = document.getElementById(\"id_FormPage_position\"); \r\n    // x.setAttribute(\"type\", \"hidden\");這是用來隱藏物件的語法\r\n     x.setAttribute( \"value\", httpGet() );\r\n    //x.value = httpGet();這是另一種更改value的方式\r\n    //x.disabled = true; 注意！表單寄送請勿設此語法，經測試很多次發現這樣會傳不出去，請改用hidden\r\n}\r\nAutoInsert_Lock_Field();', 'FormPage_js', '', 'inherit', 'closed', 'closed', '', '378-revision-v1', '', '', '2020-05-23 01:23:28', '2020-05-22 17:23:28', '', 378, 'http://127.0.0.1/2020/05/23/378-revision-v1/', 0, 'revision', '', 0),
(423, 1, '2020-05-23 10:11:44', '2020-05-23 02:11:44', '<!DOCTYPE html>\r\n<html lang=\"zh-TW\">\r\n\r\n<head>\r\n	<meta charset=\"utf-8\">\r\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n	<meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">\r\n	<link rel=\"stylesheet\" href=\"./_css/homepage3.css\" type=\"text/css\">\r\n	<title>感統</title>\r\n</head>\r\n<style>\r\n	/*整個物件的最外層容器*/\r\n	.canvas_container{\r\n		/*位置設為relative可被子層的absolute屬性區塊定位*/\r\n		position: relative;\r\n		/*自定義的RWD設定*/\r\n		max-width: 800px;\r\n		min-width:601px;\r\n		/*畫框邊線*/\r\n		border: 1px solid black;\r\n	}\r\n\r\n	/*圖片的屬性設定*/\r\n	.img_Property{\r\n		/*<img>做到RWD縮放的必要設定*/\r\n		width: 100%;\r\n		height: auto;\r\n	}\r\n\r\n	/*設定Grid格線框架*/\r\n	.grid_container{\r\n		/*位置設為absolute，便會將上層區塊的左上角作為參考點，作出相對位置的改變*/\r\n		position: absolute;\r\n		top: 0;\r\n		left: 0;\r\n		/*寬度設100%永遠填滿這個框架\r\n		\"最小高度100%\"是本物件會成功的重點，由於位置設為absolute，它會參考父層當前實際的高度作為自己的最低高度*/\r\n		width: 100%;\r\n		min-height: 100%;\r\n		/*將layout模組設為grid(格線框架)：會自動將子層區塊照自定義設定值排列*/\r\n		display: grid;\r\n		/*目前排列規則設為：4列，1fr代表全部的分割比例皆為1:1，W3Cschool會有更多這部分的變形教學*/\r\n		grid-template-rows: repeat(4, 1fr);\r\n		/*目前排列規則設為：4行*/\r\n		grid-template-columns: repeat(4, 1fr); \r\n		/*間隔：目前設為0，表示無間隔*/\r\n		grid-gap:0;\r\n	}\r\n\r\n	/*設定grid框架內的實際的方格*/\r\n	.grid_item{\r\n		/*flex是目前最優的文字排版設定，適合用在最底層*/\r\n		display: flex;\r\n		/*文字區塊xy軸都置中排列*/\r\n		justify-content: center;\r\n		align-items: center;\r\n		/*文字屬性*/\r\n		border: 1px solid white;\r\n		/*格線顏色*/\r\n		color: white;\r\n	}\r\n\r\n	/*hover表示滑鼠移置此區塊上方之後的狀態*/\r\n	.grid_item:hover{\r\n		/*滑鼠呈現手指圖案*/\r\n		cursor: pointer;\r\n		/*方格顏色轉變的顏色*/\r\n		background:rgba(0,255,0,50%);\r\n	}\r\n</style>\r\n<body>\r\n	<div class=\"canvas_container\">\r\n        <img class=\"img_Property\" \r\n            src=\"http://127.0.0.1/wp-content/uploads/2020/05/test_breakfast.jpg\"\r\n            alt=\" This is a painting picture.\">\r\n        <div id=\"id_grid_container\" class=\"grid_container\">\r\n			<!--此處用js:grid_item_generate()嵌入畫布的顯示方塊-->\r\n			<?php for($i=0;$i<16;$i++): ?>\r\n			<?php $zone=chr(ord(\'A\')+$i);?>\r\n			<div class=\'grid_item\' onclick=\"LinkTo_ChooingPage(\'<?php echo $zone;?>\')\"\r\n				onmouseenter=\"get_emptyseats(this, \'<?php echo $zone;?>\', <?php echo count_status($zone)?>)\"\r\n				onmouseleave=\"set_origin(this, \'<?php echo $zone;?>\')\">\r\n				<?php echo \"第\".$zone.\"區\"; ?>\r\n			</div>\r\n			<?php endfor?>\r\n		</div>\r\n    </div>\r\n</body>\r\n\r\n</html>\r\n<script>\r\n	function LinkTo_ChooingPage(zone) {\r\n		window.location.href = \'ChoosingPage.php?zone=\' + zone;\r\n		/*此處可直接用document.getElementById(\"id\").innerHTML=\"\";取得zone，降低程式碼複雜度*/\r\n		/*活用this可更簡化*/\r\n	}\r\n\r\n	function get_emptyseats(x, zone, number_Emptyseat) {\r\n		x.innerHTML = \'<center>第\' + zone + \'區<br>還有\' + number_Emptyseat + \'空位</center>\';\r\n	}\r\n\r\n	function set_origin(x, zone) {\r\n		x.innerHTML = \'第\' + zone + \'區\';\r\n	}\r\n\r\n	/*產生欲嵌入id=\"id_grid_container\"的可點選方格*/\r\n	function grid_item_generate() {\r\n        var i, s = \'\';\r\n		/*i可控制要顯示多少個格子*/\r\n        for (i = 0; i < 16; i++) {\r\n            s += \"<div class=\\\"grid_item\\\">\" + i + \"</div>\";\r\n        }\r\n        document.getElementById(\'id_grid_container\').innerHTML = s;\r\n    }\r\n    //grid_item_generate();\r\n</script>', 'test_universal', '', 'trash', 'closed', 'closed', '', 'test_universal__trashed', '', '', '2020-05-24 21:04:36', '2020-05-24 13:04:36', '', 0, 'http://127.0.0.1/?post_type=wbcr-snippets&#038;p=423', 0, 'wbcr-snippets', '', 0),
