(427, 1, '2020-05-23 10:16:19', '2020-05-23 02:16:19', '<!DOCTYPE html>\r\n<html lang=\"zh-TW\">\r\n\r\n<head>\r\n	<meta charset=\"utf-8\">\r\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n	<meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">\r\n	<link rel=\"stylesheet\" href=\"./_css/homepage3.css\" type=\"text/css\">\r\n	<title>感統</title>\r\n</head>\r\n<style>\r\n	/*整個物件的最外層容器*/\r\n	.canvas_container{\r\n		/*位置設為relative可被子層的absolute屬性區塊定位*/\r\n		position: relative;\r\n		/*自定義的RWD設定*/\r\n		max-width: 800px;\r\n		min-width:601px;\r\n		/*畫框邊線*/\r\n		border: 1px solid black;\r\n	}\r\n\r\n	/*圖片的屬性設定*/\r\n	.img_Property{\r\n		/*<img>做到RWD縮放的必要設定*/\r\n		width: 100%;\r\n		height: auto;\r\n	}\r\n\r\n	/*設定Grid格線框架*/\r\n	.grid_container{\r\n		/*位置設為absolute，便會將上層區塊的左上角作為參考點，作出相對位置的改變*/\r\n		position: absolute;\r\n		top: 0;\r\n		left: 0;\r\n		/*寬度設100%永遠填滿這個框架\r\n		\"最小高度100%\"是本物件會成功的重點，由於位置設為absolute，它會參考父層當前實際的高度作為自己的最低高度*/\r\n		width: 100%;\r\n		min-height: 100%;\r\n		/*將layout模組設為grid(格線框架)：會自動將子層區塊照自定義設定值排列*/\r\n		display: grid;\r\n		/*目前排列規則設為：4列，1fr代表全部的分割比例皆為1:1，W3Cschool會有更多這部分的變形教學*/\r\n		grid-template-rows: repeat(4, 1fr);\r\n		/*目前排列規則設為：4行*/\r\n		grid-template-columns: repeat(4, 1fr); \r\n		/*間隔：目前設為0，表示無間隔*/\r\n		grid-gap:0;\r\n	}\r\n\r\n	/*設定grid框架內的實際的方格*/\r\n	.grid_item{\r\n		/*flex是目前最優的文字排版設定，適合用在最底層*/\r\n		display: flex;\r\n		/*文字區塊xy軸都置中排列*/\r\n		justify-content: center;\r\n		align-items: center;\r\n		/*文字屬性*/\r\n		border: 1px solid white;\r\n		/*格線顏色*/\r\n		color: white;\r\n	}\r\n\r\n	/*hover表示滑鼠移置此區塊上方之後的狀態*/\r\n	.grid_item:hover{\r\n		/*滑鼠呈現手指圖案*/\r\n		cursor: pointer;\r\n		/*方格顏色轉變的顏色*/\r\n		background:rgba(0,255,0,50%);\r\n	}\r\n</style>\r\n<body>\r\n	<div class=\"canvas_container\">\r\n        <img class=\"img_Property\" \r\n            src=\"http://127.0.0.1/wp-content/uploads/2020/05/test_breakfast.jpg\"\r\n            alt=\" This is a painting picture.\">\r\n        <div id=\"id_grid_container\" class=\"grid_container\">\r\n			<!--此處用js:grid_item_generate()嵌入畫布的顯示方塊-->\r\n			<?php for($i=0;$i<16;$i++): ?>\r\n			<?php $zone=chr(ord(\'A\')+$i);?>\r\n			<div class=\'grid_item\' onclick=\"LinkTo_ChooingPage(\'<?php echo $zone;?>\')\"\r\n				onmouseenter=\"get_emptyseats(this, \'<?php echo $zone;?>\', <?php echo count_status($zone)?>)\"\r\n				onmouseleave=\"set_origin(this, \'<?php echo $zone;?>\')\">\r\n				<?php echo \"第\".$zone.\"區\"; ?>\r\n			</div>\r\n			<?php endfor?>\r\n		</div>\r\n    </div>\r\n</body>\r\n\r\n</html>\r\n<script>\r\n	function LinkTo_ChooingPage(zone) {\r\n		window.location.href = \'ChoosingPage.php?zone=\' + zone;\r\n		/*此處可直接用document.getElementById(\"id\").innerHTML=\"\";取得zone，降低程式碼複雜度*/\r\n		/*活用this可更簡化*/\r\n	}\r\n\r\n	function get_emptyseats(x, zone, number_Emptyseat) {\r\n		x.innerHTML = \'<center>第\' + zone + \'區<br>還有\' + number_Emptyseat + \'空位</center>\';\r\n	}\r\n\r\n	function set_origin(x, zone) {\r\n		x.innerHTML = \'第\' + zone + \'區\';\r\n	}\r\n\r\n	/*產生欲嵌入id=\"id_grid_container\"的可點選方格*/\r\n	function grid_item_generate() {\r\n        var i, s = \'\';\r\n		/*i可控制要顯示多少個格子*/\r\n        for (i = 0; i < 16; i++) {\r\n            s += \"<div class=\\\"grid_item\\\">\" + i + \"</div>\";\r\n        }\r\n        document.getElementById(\'id_grid_container\').innerHTML = s;\r\n    }\r\n    //grid_item_generate();\r\n</script>', 'test_universal', '', 'inherit', 'closed', 'closed', '', '423-revision-v1', '', '', '2020-05-23 10:16:19', '2020-05-23 02:16:19', '', 423, 'http://127.0.0.1/2020/05/23/423-revision-v1/', 0, 'revision', '', 0),
(428, 1, '2020-05-23 10:21:47', '2020-05-23 02:21:47', '/*(啟用session以使用php全域session變數)*/\r\n@session_start();\r\n\r\n/*=================== Highlight(變數解釋) =====================*/\r\n/*  \r\n * 區碼編號：$zone，由英文字母A-Z所構成的字串\r\n * 位置編號：$pos，由\"$zone-digits\"所構成的字串，例如: A-01\r\n * 畫布目前設為兩層的結構，共有16x16=256個區塊可供選擇，\r\n * 第一層用英文字母分區，第二層依區碼顯示完整的位置編號，\r\n * 此設計是參考目前商業界演唱會訂位系統的設計所製作，可保證使用者體驗佳\r\n*/\r\n/*=================== My functions ===========================*/\r\n/*\r\n**  函式名稱：get_status_permition($pos)\r\n**  [用途：查詢位置是否許可]\r\n**  輸入: 畫布區塊的位置編號(String)...例如查詢單筆:\'A-01\' 或 查詢多筆:\'A\'\r\n**  回傳: 位置的狀態(integer)...1表示空位(允許)/ -1表示已有登記(禁止)/ 0正在被報名(鎖定)\r\n**  程式要點： 資料庫會回傳時間字串，用時間來判斷此位置的狀態為何\r\n**          (1)time=2222/1/1 00:00:00，(設定為100年後)表示此位置已被登記-> -1\r\n**          (2)time>現在時間，表示此位置正在被報名-> 0\r\n**          (3)time<現在時間，表示此位置是空位-> 1\r\n*/\r\nfunction get_status_permition($pos){\r\n  /*--資料庫搜尋--*/\r\n  /*$array_DB_return用來接收含有回傳的time資料*/\r\n  $array_DB_return = array();\r\n  /*$sql是SQL操作指令*/\r\n  $sql = \"SELECT STATUS FROM `booking_info` WHERE POS LIKE (\'$pos%\')\";\r\n  /*mysqli_query()執行SQL操作，select指令會回傳SQL分類結果陣列，其他指令回傳True*/\r\n  $rst =@mysqli_query($_SESSION[\'con\'], $sql);\r\n  if($rst){\r\n    /*mysqli_num_rows($rst)回傳result矩陣的列數量*/\r\n    if(mysqli_num_rows($rst) > 0){ \r\n      /*讀取陣列資料：mysqli_fetch_assoc($rst)是以欄位名稱作為索引標籤，並回傳矩陣*/\r\n      for($i = 0; $row= mysqli_fetch_assoc($rst); $i++){\r\n        $array_DB_return[] = $row;\r\n        /*判斷該位置紀錄的時間 是否小於 當前時間*/\r\n        if( strtotime($array_DB_return[$i][\'STATUS\']) < strtotime(date(\"Y-m-d H:i:s\")) ){\r\n          /*在$array_DB_return[$i]後面push一個node[permit]，用來記錄允許與否*/\r\n          $array_DB_return[$i] += [ \"permit\" => 1];\r\n        }elseif( strtotime($array_DB_return[$i][\'STATUS\']) == strtotime(\"2222-01-01 00:00:00\") ){\r\n          $array_DB_return[$i] += [ \"permit\" => -1];\r\n        }else{\r\n          $array_DB_return[$i] += [ \"permit\" => 0];             \r\n        }\r\n      }\r\n    }\r\n    mysqli_free_result($rst);\r\n  }else{echo \"{$sql} comes out error\".mysqli_error($_SESSION[\'con\']);}\r\n  // echo \"(Debug) *count:* \".count($array_DB_return).\"<br>\";\r\n  // echo \"(Debug) *print_r:* \";print_r($array_DB_return);echo \"<br>\";\r\n\r\n  /*--回傳資料--*/\r\n  if($i < 2){\r\n    return $array_DB_return[0][\'permit\'];/*單筆資料搜索值接回傳字串*/\r\n  }else{\r\n    return $array_DB_return;/*多筆資料搜索回傳陣列型態，需要自行拆解*/\r\n  }\r\n}\r\n\r\n/*get_position_and_status\r\n**  函式名稱：get_position($zone)\r\n**  [用途：查詢該分區中所有的位置編號]\r\n**  輸入: 畫布區塊的區碼編號(String)...例如\'A\'\r\n**  回傳: 所有位置編號的二維陣列(Array)\r\n**  程式要點：個別資料存於array[index][POS]，另一端接收資料後要用foreach拆解\r\n*/\r\nfunction get_position($zone){\r\n  $datas = array();\r\n  $sql = \"SELECT POS FROM `booking_info` WHERE POS LIKE (\'$zone%\')\";\r\n  $rst =@mysqli_query($_SESSION[\'con\'], $sql);\r\n\r\n  if($rst){\r\n    if(mysqli_num_rows($rst) > 0){ /*mysqli_num_rows($rst)回傳result矩陣的列數量*/\r\n      while ($row = mysqli_fetch_assoc($rst)){ \r\n        $datas[] = $row;\r\n      }\r\n    }\r\n    mysqli_free_result($rst);\r\n  }else{echo \"{$sql} comes out error\".mysqli_error($_SESSION[\'con\']);}\r\n  //echo \"(Debug) \";print_r($datas);echo \"<br>\";\r\n  return $datas;\r\n}\r\n\r\n/**edit_statusToblock\r\n * 函式名稱：set_status_blocking($pos, $addMinute)\r\n * [用途：當使用者進入填寫表單頁面，要將該位置設為\"鎖定\"，因為報名過程中要防止其他人Overbooking]\r\n * 輸入:$pos位置編號、$addMinute要暫時鎖定多少分鐘\r\n * 回傳:無\r\n * 程式要點：此設計的優點是若時間到要解除鎖定，毋須再更新資料庫，\r\n *          隨著世界運轉，封鎖會自動過期，只要搭配get_status_permition()更新前端的\r\n *          顯示狀態即可。同樣道理若將時間設為一個生命無法到達的時間，相當於永久封鎖。\r\n */\r\nfunction set_status_blocking($pos, $addMinute){\r\n  $blockingTime = new datetime();\r\n  $addTime = \'P0Y0M0DT0H\'.(string)$addMinute.\'M0S\';\r\n  $blockingTime->add(new DateInterval($addTime));\r\n  echo $blockingTime->format(\"Y-m-d H:i:s\");\r\n  $sql = \"UPDATE `booking_info` SET `STATUS`= \'$blockingTime\' WHERE `POS`=\'$pos\'\";\r\n}\r\n\r\n/**count_status()\r\n * 函式名稱：get_status_permition_count($zone)\r\n * [用途：目前用於首頁畫面，計算每個區有尚有多少個空位]\r\n * 輸入:$zone 區域編碼(string)\r\n * 回傳:$count 數量(int or string?)\r\n * 程式要點：盡量在資料庫搜尋時就先過篩，減少傳遞的資料size，加快後台資料處理速度\r\n */\r\nfunction get_status_permition_count($zone){\r\n  /*根據位置區碼，搜尋資料庫小於目前時間的資料，並回傳其數量(陣列形式)*/\r\n  $sql = \"SELECT COUNT(`SER`) FROM `booking_info` WHERE `POS` LIKE \'$zone%\' AND `STATUS`<CURRENT_TIMESTAMP \";\r\n  $result  =@mysqli_query($_SESSION[\'con\'], $sql);\r\n  if($result){\r\n    if(mysqli_num_rows($result) > 0){ /*function mysqli_num_rows($result)回傳result矩陣的列數量*/\r\n      $count = mysqli_fetch_assoc($result);\r\n    }\r\n    mysqli_free_result($result );\r\n  }else{echo \"{$sql} comes out error!!<br>\".mysqli_error($_SESSION[\'con\']);}\r\n\r\n  return $count[\'COUNT(`SER`)\'];\r\n}\r\n\r\n/**\r\n * (選擇性的功能)\r\n * [用途：客製化後端控制台的訊息框]\r\n * 程式要點：直接編寫HTML即可，比對一下控制台的畫面，this is not too difficult to understand.\r\n*/\r\nfunction TGW_dashboard_widgets(){\r\n  global $wp_meta_boxes;\r\n  wp_add_dashboard_widget(\'TGW_help_widget\',\"來自網頁開發者的提醒\",\'TGW_dashboard_help\');\r\n}\r\nfunction TGW_dashboard_help(){\r\n  echo \'<p><b>To whom it may concern:</b></p>\r\n  <p>若您是一般網頁管理者：Wordpress是一個毋需網頁程式語言能力亦可輕鬆調整網頁介面的開發框架，\r\n  你可以自動略過任何有關程式碼的部分，透過左邊選單中的Elementor插件功能即可隨心所欲、安全地編輯網頁。</p>\r\n  <p>若您是程式設計師：由於wordpress(以下簡稱WP)是整合框架，請善用WP所提供完善的API進行一切程式碼的編修，。\r\n  為了保護程式重要資料，<b>建議勿直接修改WP資料夾</b>內部檔案的程式碼，\r\n  例如直接修改主題(Theme/tamplate)的程式碼，若此主題的原開發者推出更新版本，可能導致的自定義的程式碼被洗掉。</p>\r\n  <p>因此若需要自定義任何程式碼(html, Javascript, CSS, php)有以下幾種方式：</p>\r\n  <p>(1)任何頁面的物件都具備<b>自定義CSS(Additional CSS class)</b>、<b>html修改</b>(Edit as html)</p>\r\n  <p>(2)若需要全域套用CSS：點選做側欄->外觀->自訂->附加的CSS，此處可以針對整個模板做CSS調整</p>\r\n  <p>(3)任何你看到可以編寫html語法的地方都可以編寫javascript</p>\r\n  <p>(4)若想引入自己撰寫的php page(參雜php語法及html語法的檔案)，可以透過外掛插件<b>Insert PHP Code Snippet</b>，\r\n  該插件允許編寫php檔案，你可以將html語法用echo回傳，最後儲存後你會得到一段中誇號短語法，你可以在頁面加入shortcode物件，並貼上該語法，就能達到嵌入php page的效果</p>\r\n  <p>(5)若想新增自製的php library，可以使用插件<b>我的自訂功能(my custom functions)</b>此處撰寫的php function或執行的method都會直接反映在網頁上</p>\r\n  <p><b>目前使用的外掛插件有Elementor(比WP原生的編輯器更佳、易上手的GUI編輯器)、Insert PHP Code Snippet、my custom functions</b></p>\r\n  <p>若操作上任何疑問，請洽東華大學社參辦(XXXXXXXX)</p>\r\n  <p>上位網頁開發者：Kuihao(41053A041@gms.ndhu.edu.tw)</p>\r\n  <p><em>若想更改此段訊息，請於左欄點選Woody snippets，找尋並編輯\"my_php_function\"，\r\n  最後搜尋function TGW_dashboard_help()即可編輯這段文字</em></p>\';\r\n}\r\nadd_action(\'wp_dashboard_setup\',\'TGW_dashboard_widgets\');\r\n\r\n/**\r\n * 函式名稱：set_block($x, $y)\r\n * [用途：為資料庫新增x*y筆初始化位置資料，用於開設新的畫布頁面或重設資料庫時，可能用得上]\r\n * 輸入:$x、$y...x*y即完全平方數，例如：256筆資料是16*16、36筆資料是6*6\r\n * 回傳:無\r\n * 程式要點：注意!!x、y不是列*欄，第一個變數會轉成英文字母\r\n */\r\n// function set_block($x, $y)\r\n// {\r\n// 	$sql=\'\';\r\n// 	for($i=0;$i<$x;$i++){\r\n// 		$pos_row=chr(ord(\'A\')+$i);\r\n// 		for($j=1;$j<=$y;$j++){\r\n//       if($j<=9):\r\n//         $pos_col=\'0\'.$j;\r\n//       else:\r\n//         $pos_col=$j;\r\n//       endif;\r\n//       $pos=$pos_row.\"-\".$pos_col;\r\n\r\n//     	$sql =\"INSERT INTO booking_info(POS,STATUS) VALUES (\'{$pos}\',\'0\')\";\r\n//       echo \"{$pos}.OK<br>\";\r\n// 			$rst=@mysqli_query($_SESSION[\'con\'],$sql);\r\n//     	if($rst){echo \"{$pos}.OK\";}else{\"{$pos}.fail\";}\r\n// 		}\r\n// 	}\r\n// }\r\n\r\n/**\r\n * 更新日期：2020/05/23 連接新資料庫、優化SQL搜尋、\r\n *                     降低函式的耦合性(Coupling)、大幅提高內聚性(Cohesion)、\r\n *                     新增WP風格的註解\r\n */', 'my_php_function', '', 'trash', 'closed', 'closed', '', 'my_php_function__trashed', '', '', '2020-05-23 22:39:19', '2020-05-23 14:39:19', '', 0, 'http://127.0.0.1/?post_type=wbcr-snippets&#038;p=428', 0, 'wbcr-snippets', '', 0),
(429, 1, '2020-05-23 10:21:47', '2020-05-23 02:21:47', '/*(啟用session以使用php全域session變數)*/\r\n\r\n/*連線至mysql管理系統中的wpdb資料庫(wpdb是存放Wordpress網站資料的資料庫)*/\r\n@session_start();\r\n$_SESSION[\'2con\']=@mysqli_connect(\"127.0.0.1\", \"root\", \"\", \"wpdb\") or die(\"NO\");\r\nif($_SESSION[\'2con\']){mysqli_query($_SESSION[\'2con\'], \"SET NAMES utf8\");}\r\nelse{echo \'無法連線mysql資料庫 :<br/>\' . mysqli_connect_error();}\r\n\r\n/*------------------old fun following--------------------*/\r\n/*連線至TGW資料庫(目前是連至bookingDB)*/\r\n$_SESSION[\'con\']=@mysqli_connect(\"127.0.0.1\", \"root\", \"\", \"booking\") or die(\"NO\");\r\nif($_SESSION[\'con\']){mysqli_query($_SESSION[\'con\'], \"SET NAMES utf8\");}\r\nelse{echo \'無法連線mysql資料庫 :<br/>\' . mysqli_connect_error();}\r\n\r\n/*(可選擇的指令)讓網頁前端隱藏wordpress meta資料*/\r\nremove_action(\'wp_head\', \'wp_generator\');\r\n\r\n/*(可選擇的指令)客製化控制台訊息框*/\r\nfunction TGW_dashboard_widgets(){\r\n    global $wp_meta_boxes;\r\n    wp_add_dashboard_widget(\'TGW_help_widget\',\"來自網頁開發者的提醒\",\'TGW_dashboard_help\');\r\n}\r\nfunction TGW_dashboard_help(){\r\n    echo \'<p><b>To whom it may concern:</b></p>\r\n    <p>若您是一般網頁管理者：Wordpress是一個毋需網頁程式語言能力亦可輕鬆調整網頁介面的開發框架，\r\n    你可以自動略過任何有關程式碼的部分，透過左邊選單中的Elementor插件功能即可隨心所欲、安全地編輯網頁。</p>\r\n    <p>若您是程式設計師：由於wordpress(以下簡稱WP)是整合框架，請善用WP所提供完善的API進行一切程式碼的編修，。\r\n    為了保護程式重要資料，<b>建議勿直接修改WP資料夾</b>內部檔案的程式碼，\r\n    例如直接修改主題(Theme/tamplate)的程式碼，若此主題的原開發者推出更新版本，可能導致的自定義的程式碼被洗掉。</p>\r\n    <p>因此若需要自定義任何程式碼(html, Javascript, CSS, php)有以下幾種方式：</p>\r\n    <p>(1)任何頁面的物件都具備<b>自定義CSS(Additional CSS class)</b>、<b>html修改</b>(Edit as html)</p>\r\n    <p>(2)若需要全域套用CSS：點選做側欄->外觀->自訂->附加的CSS，此處可以針對整個模板做CSS調整</p>\r\n    <p>(3)任何你看到可以編寫html語法的地方都可以編寫javascript</p>\r\n    <p>(4)若想引入自己撰寫的php page(參雜php語法及html語法的檔案)，可以透過外掛插件<b>Insert PHP Code Snippet</b>，\r\n    該插件允許編寫php檔案，你可以將html語法用echo回傳，最後儲存後你會得到一段中誇號短語法，你可以在頁面加入shortcode物件，並貼上該語法，就能達到嵌入php page的效果</p>\r\n    <p>(5)若想新增自製的php library，可以使用插件<b>我的自訂功能(my custom functions)</b>此處撰寫的php function或執行的method都會直接反映在網頁上</p>\r\n    <p><b>目前使用的外掛插件有Elementor(比WP原生的編輯器更佳、易上手的GUI編輯器)、Insert PHP Code Snippet、my custom functions</b></p>\r\n    <p>若操作上任何疑問，請洽東華大學社參辦(XXXXXXXX)</p>\r\n    <p>上位網頁開發者：Kuihao(41053A041@gms.ndhu.edu.tw)</p>\';\r\n}\r\nadd_action(\'wp_dashboard_setup\',\'TGW_dashboard_widgets\');\r\n\r\n/*(需要connection/session)輸入畫布位置、資料庫回傳位置的狀態(允許、禁止、鎖定)*/\r\nfunction get_status($pos){\r\n  $data=0;\r\n  /*--$sql是SQL操作指令--*/\r\n  $sql = \"SELECT STATUS FROM `booking_info` WHERE POS LIKE (\'$pos\')\";\r\n  /*--mysqli_query()執行SQL操作，select指令會回傳SQL分類結果陣列，其他指令回傳True--*/\r\n  $rst =@mysqli_query($_SESSION[\'con\'], $sql);\r\n  if($rst){\r\n    $data=mysqli_fetch_assoc($rst);/*讀取陣列資料：mysqli_fetch_assoc($rst)是以欄位名稱作為索引標籤，並回傳矩陣*/\r\n    //print_r($rst);\r\n    //echo \'<br>\';\r\n    //print_r($data);\r\n  }else{echo \"{$sql} comes out error\".mysqli_error($_SESSION[\'con\']);}\r\n  mysqli_free_result($rst);\r\n  return $data[\'STATUS\'];\r\n}\r\n\r\n/*(需要connection/session)輸入區碼、資料庫回傳狀態、位置*/\r\n/*get the position and status of blocks. status:0=empty,1=occupied,-1=temprary lock*/\r\nfunction get_position_and_status($zone){\r\n  $datas = array();\r\n  $sql = \"SELECT POS,STATUS FROM `booking_info` WHERE POS LIKE (\'$zone%\')\";\r\n  $rst =@mysqli_query($_SESSION[\'con\'], $sql);\r\n\r\n  if($rst){\r\n    if(mysqli_num_rows($rst) > 0){ /*mysqli_num_rows($rst)回傳result矩陣的列數量*/\r\n      while ($row = mysqli_fetch_assoc($rst)){ \r\n        $datas[] = $row;\r\n      }\r\n    }\r\n    mysqli_free_result($rst);\r\n  }else{echo \"{$sql} comes out error\".mysqli_error($_SESSION[\'con\']);}\r\n  //$_SESSION[\'count\']=count($datas);\r\n  //echo $_SESSION[\'count\'];\r\n  //print_r($datas);\r\n  return $datas;\r\n}\r\n\r\n/*(需要connection/session)位置初始化：為資料庫新增x*y筆位置資料 Block means Art works, this.f() use to insert x*y blank blocks to DB */\r\nfunction set_block($x, $y)\r\n{\r\n	$sql=\'\';\r\n	for($i=0;$i<$x;$i++){\r\n		$pos_row=chr(ord(\'A\')+$i);\r\n		for($j=1;$j<=$y;$j++){\r\n      if($j<=9):\r\n        $pos_col=\'0\'.$j;\r\n      else:\r\n        $pos_col=$j;\r\n      endif;\r\n      $pos=$pos_row.\"-\".$pos_col;\r\n\r\n    	$sql =\"INSERT INTO booking_info(POS,STATUS) VALUES (\'{$pos}\',\'0\')\";\r\n      echo \"{$pos}.OK<br>\";\r\n			$rst=@mysqli_query($_SESSION[\'con\'],$sql);\r\n    	if($rst){echo \"{$pos}.OK\";}else{\"{$pos}.fail\";}\r\n		}\r\n	}\r\n}\r\n\r\n/*(需要connection/session)狀態更新：占用(STATUS=-1)*/\r\n/*位置正在被選取，將資料庫位置狀態設為-1*/\r\nfunction edit_statusToblock($pos){\r\n$sql = \"UPDATE `booking_info` SET `STATUS`=-1 WHERE `POS`=\'$pos\'\";\r\n}\r\n\r\n/*這有問題 防止表單重複提交機制要更動*/\r\nfunction form_echo_check( $var ){\r\n  if(!empty($_SESSION[$var]))\r\n  return $_SESSION[$var];\r\n}\r\n\r\n/*(需要connection/session)用於首頁，計算該區域有多少空位可以使用*/\r\nfunction count_status($zone){\r\n  $sql = \"SELECT `STATUS` FROM `booking_info` WHERE POS LIKE (\'$zone%\')\";\r\n  $rst =@mysqli_query($_SESSION[\'con\'], $sql);\r\n  \r\n  $count=0;/*計算空位的數量*/\r\n  \r\n  if($rst){\r\n    if(mysqli_num_rows($rst) > 0){ /*mysqli_num_rows($rst)回傳result矩陣的列數量*/\r\n      \r\n      while ($one_row = mysqli_fetch_assoc($rst)){ \r\n        if($one_row[\'STATUS\']==0)$count++;/*若狀態為0表示是空位，conut+1*/\r\n      }\r\n    }\r\n    mysqli_free_result($rst);\r\n  }else{echo \"{$sql} comes out error\".mysqli_error($_SESSION[\'con\']);}\r\n\r\n  return $count;\r\n}\r\n\r\n/*表單驗證之用，防止使用者特殊輸入*/\r\nfunction test_input($data) {\r\n  if( is_array($data)){\r\n    foreach($data as $key => $value){\r\n      $value = trim($value);/*去除用户输入数据中不必要的字符（多余的空格、制表符、换行）*/\r\n      $value = htmlspecialchars($value);/*轉換HTML標籤語法*/\r\n      $value = stripslashes($value);/*删除用户输入数据中的反斜杠（\\）*/\r\n      $data[$key] = $value;\r\n    }\r\n  }else{\r\n    $data = trim($data);/*去除用户输入数据中不必要的字符（多余的空格、制表符、换行）*/\r\n    $data = stripslashes($data);/*删除用户输入数据中的反斜杠（\\）*/\r\n    $data = htmlspecialchars($data);/*轉換HTML標籤語法*/\r\n  }\r\n\r\n	return $data;\r\n }\r\n\r\n/*用於表單性別*/\r\nfunction table_gender($data){\r\n  $word=\"\";\r\n  switch($data){\r\n    case \'Male\':\r\n      $word = \'男\';\r\n      break;\r\n    case \'Female\':\r\n      $word = \'女\';\r\n      break;\r\n    case \'Other\':\r\n      $word = \'其他\';  \r\n      break;\r\n    case \'Secret\':\r\n      $word = \'不公開\';\r\n      break;\r\n    default:\r\n      $word = \'存取錯誤\';\r\n  }\r\n  return $word;\r\n}\r\n\r\n/*用於表單提醒方式*/\r\nfunction table_note($datas){\r\n  $word=\"\";\r\n  foreach($datas as $onedata)\r\n  switch($onedata){\r\n    case \'email\':\r\n      $word = $word.\'Email \';\r\n      break;\r\n    case \'LINE\':\r\n      $word = $word.\'LINE \';\r\n      break;\r\n    case \'phonemessage\':\r\n      $word = $word.\'手機簡訊 \';  \r\n      break;\r\n    default:\r\n      $word = \'存取錯誤\';\r\n  }\r\n  return $word;\r\n}\r\n\r\n/*測試php server, 成功會回傳雙引號及單引號的特性*/\r\nfunction K_testing(){\r\n    $test_var=100;\r\n    echo \"Double quotes: $test_var<br>\";\r\n    echo \'Apostrophe: $test_var\';\r\n}\r\n\r\n/*測試mysql+php server, 成功會回傳資料庫中流水號為1的位置資料*/\r\nfunction DB_testing(){\r\n  /*--data裝DB回傳資料--*/\r\n  $data=0;\r\n  /*--$sql存欲操作DB的SQL操作指令(現行官方用語叫query)--*/\r\n  $sql = \"SELECT `POS` FROM `booking_info` WHERE SER = \'1\'\";\r\n  /*--mysqli_query()執行傳入的SQL指令，回傳SQL分類結果陣列，其他指令回傳True--*/\r\n  $rst =@mysqli_query($_SESSION[\'con\'], $sql);\r\n  if($rst){\r\n    $data=mysqli_fetch_assoc($rst);/*讀取陣列資料：mysqli_fetch_assoc($rst)是以欄位名稱作為索引標籤，並回傳矩陣*/\r\n    print_r($rst);\r\n    echo \'<br>\';\r\n    print_r($data);\r\n  }else{echo \"{$sql} comes out error\".mysqli_error($_SESSION[\'con\']);}\r\n  mysqli_free_result($rst);\r\n  return $data[\'POS\'];\r\n}\r\n\r\n\r\n\r\n?>', 'my_php_function', '', 'inherit', 'closed', 'closed', '', '428-revision-v1', '', '', '2020-05-23 10:21:47', '2020-05-23 02:21:47', '', 428, 'http://127.0.0.1/2020/05/23/428-revision-v1/', 0, 'revision', '', 0),
