(747, 1, '2020-05-24 16:31:41', '2020-05-24 08:31:41', '<script>\r\n/*請呼叫JS_call_jQuery()執行更新DB位置狀態的涵式*/\r\n/*ajax傳送路徑是自定義的php file*/\r\n	\r\n/* 以下為jQuery語法(要使用ajax功能) */\r\n/*當文件準備好*/$(document).on(\"ready\", function(){/*此處可以加入自定義event*/});\r\n$.AjaxToServer = function() {\r\n    $.ajax({\r\n        type: \"POST\",\r\n        url: \"./../../../_CurrentVerson_Website_Data/php/K_testing_Ajax.php\",\r\n        data: {\r\n            n: 200\r\n        },\r\n        dataType: \'html\' //設定該網頁回應的會是 html 格式\r\n    }).done(function (data) {\r\n        //成功的時候回傳資料過來用argument:data來接，並可在這裡執行顯示處理\r\n        console.log(data);\r\n        // if (data == \"yes\") {\r\n        //     alert(\"註冊成功，將自動前往登入頁。\");\r\n        //     //註冊新增成功，轉跳到登入頁面。\r\n        //     window.location.href = \"admin/login.php\";\r\n        // } else {\r\n        //     alert(\"註冊失敗，請與系統人員聯繫\");\r\n        // }\r\n    }).fail(function (jqXHR, textStatus, errorThrown) {\r\n        //失敗的時候\r\n        // alert(\"有錯誤產生，請看 console log\");\r\n        console.log(jqXHR.responseText);\r\n    });\r\n    //一樣要回傳 false 阻止 from 繼續把資料送出去。因為會交由上方的 ajax 非同步處理註冊的動作\r\n    return false;\r\n};\r\n\r\n// 建立 Ajax 物件\r\nrequest = new ajaxRequest();\r\nfunction ajaxRequest() {\r\n    try {\r\n        // （除 IE7 之前）支援所有現代瀏覽器\r\n        var request = new XMLHttpRequest();\r\n    } catch (e1) {\r\n        try {\r\n            // （支援 IE6）如果有的話就用 ActiveX 物件的最新版本\r\n            request = new ActiveXObject(\"Msxml2.XMLHTTP.6.0\");\r\n        } catch (e2) {\r\n            try {\r\n                // （支援 IE5）否則就用較舊的版本\r\n                request = new ActiveXObject(\"Msxml2.XMLHTTP.3.0\");\r\n            } catch (e3) {\r\n                // 不支援 Ajax，拋出錯誤\r\n                throw new Error(\"XMLHttpRequest is not supported\");\r\n            }\r\n        }\r\n    }\r\n    return request;\r\n}\r\n\r\n/*由Javascript呼叫jQuery function*/\r\nfunction JS_call_jQuery(){\r\n    $.AjaxToServer();\r\n}\r\n</script>', 'FormPage_AJAX', '', 'inherit', 'closed', 'closed', '', '713-revision-v1', '', '', '2020-05-24 16:31:41', '2020-05-24 08:31:41', '', 713, 'http://127.0.0.1/2020/05/24/713-revision-v1/', 0, 'revision', '', 0);
