  	if(flag){\r\n    	$(\"#id_FormPage_submit_bt\").click();\r\n    }\r\n  });\r\n\r\n/*當表單送出之後、寄信之前，對DB更新欄位的效期，更多API可查詢Contact Form 7: DOM events*/\r\ndocument.addEventListener(\'wpcf7mailsent\', function (event) {\r\n  if (event.detail.contactFormId == \'242\') {\r\n    /*更新DB的位置效期(設為已劃位)*/\r\n    JS_call_jQuery();\r\n    window.location.href = \"http://127.0.0.1/%e6%84%9f%e6%81%a9%e7%89%86-%e6%81%ad%e5%96%9c%e5%8a%83%e4%bd%8d%e9%a0%81/\";\r\n\r\n    //var Booked_position = document.getElementById(\"id_FormPage_position\").value;\r\n    //console.log(event.detail);//將cf7ajax的資料紀錄於brownser console\r\n    //alert(document.getElementById(\"id_form_name\").value);//跳出名字欄位的資料\r\n  }\r\n}, false);\r\n\r\n', 'FormPage_js', '', 'inherit', 'closed', 'closed', '', '378-revision-v1', '', '', '2020-05-24 22:44:14', '2020-05-24 14:44:14', '', 378, 'http://127.0.0.1/2020/05/24/378-revision-v1/', 0, 'revision', '', 0),
