(790, 1, '2020-05-25 00:51:41', '2020-05-24 16:51:41', '/*當格子被點選後，連結至報名表單頁(注意若更改頁面名稱後，此網址也要更換)*/\r\nfunction LinkTo_BookingPage(pos) {\r\n    /*此連結使用HTTP GET require傳送參數，請保留?pos=\'+pos*/\r\n    window.location.href = \'http://127.0.0.1/%e5%8a%83%e4%bd%8d%e8%b3%87%e6%96%99%e8%a1%a8%e5%96%ae/?pos=\'+pos;\r\n}\r\n\r\n/*依據DB回傳之位置狀態的不同，給予不同的顯示調整*/\r\nfunction show_state(x, status, pos){\r\n    switch (status) {\r\n        case \'1\':\r\n            x.innerHTML = \'<center>\' + pos + \':空位</center>\';\r\n            break;\r\n        case \'-1\':\r\n            x.innerHTML = \'<center>\' + pos + \':已被劃記</center>\';\r\n            break;\r\n        case \'0\':\r\n            x.innerHTML = \'<center>\' + pos + \':被報名中</center>\';\r\n            break;\r\n        default:\r\n            x.innerHTML = \'<center>\' + pos + \'<br>Something wrong!</center>\';\r\n    }\r\n}\r\n\r\n/*當滑鼠離開格子之後，讓顯示恢復原設定*/\r\nfunction set_origin(x, pos){\r\n    x.innerHTML=pos;\r\n}\r\n\r\n// var zone_js = location.search;\r\n// 根據ID插入html\r\n// function insert_div(){\r\n//     var insertDiv = document.getElementById(\"id\");\r\n//     insertDiv.innerHTML = \"\";\r\n// }\r\n\r\n', 'ChoosePage_js', '', 'inherit', 'closed', 'closed', '', '210-revision-v1', '', '', '2020-05-25 00:51:41', '2020-05-24 16:51:41', '', 210, 'http://127.0.0.1/2020/05/25/210-revision-v1/', 0, 'revision', '', 0),
