(819, 1, '2020-05-29 20:08:23', '2020-05-29 12:08:23', '\r\n/*當格子被點選後，連結至選擇位置頁*/\r\nfunction LinkTo_ChooingPage(zone){\r\n    /*抓取Protocol與Host*/\r\n    var GetProtocol = window.location.protocol;\r\n    var GetHost = window.location.host;\r\n    /*此連結使用HTTP GET require傳送參數，請保留?zone=\'+zone;*/\r\n    window.location.href = GetProtocol+GetHost+\'/%e6%84%9f%e6%81%a9%e7%89%86%e9%81%b8%e6%93%87%e4%bd%8d%e7%bd%ae%e9%a0%81/?zone=\'+zone;\r\n    /*此處也可直接用document.getElementById(\"id\").innerHTML=\"\";取得zone，降低程式碼複雜度*/\r\n    /*若活用this可更簡化*/\r\n}\r\n\r\n/*顯示目前格子代表的區域內還有多少空位*/\r\nfunction get_emptyseats(x, zone, number_Emptyseat){\r\n    x.innerHTML=\'<center>第\'+zone+\'區<br>\'+number_Emptyseat+\'空位</center>\';\r\n}\r\n\r\n/*當滑鼠離開格子之後，讓顯示恢復原設定*/\r\nfunction set_origin(x, zone){\r\n    x.innerHTML=\'第\'+zone+\'區\';\r\n}\r\n', 'Homepage_Javascript', '', 'inherit', 'closed', 'closed', '', '111-revision-v1', '', '', '2020-05-29 20:08:23', '2020-05-29 12:08:23', '', 111, 'http://127.0.0.1/2020/05/29/111-revision-v1/', 0, 'revision', '', 0),
(820, 1, '2020-05-29 20:11:16', '2020-05-29 12:11:16', '/*當格子被點選後，連結至報名表單頁*/\r\nfunction LinkTo_BookingPage(pos) {\r\n    /*抓取Protocol與Host*/\r\n    var GetProtocol = window.location.protocol;\r\n    var GetHost = window.location.host;\r\n    /*此連結使用HTTP GET require傳送參數，請保留?pos=\'+pos*/\r\n    window.location.href = GetProtocol+GetHost+\'/%e5%8a%83%e4%bd%8d%e8%b3%87%e6%96%99%e8%a1%a8%e5%96%ae/?pos=\'+pos;\r\n}\r\n\r\n/*依據DB回傳之位置狀態的不同，給予不同的顯示調整*/\r\nfunction show_state(x, status, pos){\r\n    switch (status) {\r\n        case \'1\':\r\n            x.innerHTML = \'<center>\' + pos + \':空位</center>\';\r\n            break;\r\n        case \'-1\':\r\n            x.innerHTML = \'<center>\' + pos + \':已被劃記</center>\';\r\n            break;\r\n        case \'0\':\r\n            x.innerHTML = \'<center>\' + pos + \':被報名中</center>\';\r\n            break;\r\n        default:\r\n            x.innerHTML = \'<center>\' + pos + \'<br>Something wrong!</center>\';\r\n    }\r\n}\r\n\r\n/*當滑鼠離開格子之後，讓顯示恢復原設定*/\r\nfunction set_origin(x, pos){\r\n    x.innerHTML=pos;\r\n}\r\n\r\n\r\n', 'ChoosePage_js', '', 'inherit', 'closed', 'closed', '', '210-revision-v1', '', '', '2020-05-29 20:11:16', '2020-05-29 12:11:16', '', 210, 'http://127.0.0.1/2020/05/29/210-revision-v1/', 0, 'revision', '', 0),
