(849, 1, '2020-05-31 14:37:16', '2020-05-31 06:37:16', '/*當格子被點選後，連結至報名表單頁*/\r\nfunction LinkTo_BookingPage(pos) {\r\n    /*抓取Protocol與Host*/\r\n    var GetProtocol = window.location.protocol;\r\n    var GetHost = window.location.host;\r\n    /*此連結使用HTTP GET require傳送參數，請保留?pos=\'+pos*/\r\n    window.location.href = GetProtocol+GetHost+\'/%e5%8a%83%e4%bd%8d%e8%b3%87%e6%96%99%e8%a1%a8%e5%96%ae/?pos=\'+pos;\r\n}\r\n\r\n/*依據DB回傳之位置狀態的不同，給予不同的顯示調整*/\r\nfunction show_state(x, status, pos){\r\n    switch (status) {\r\n        case \'1\':\r\n            x.innerHTML = \'<center>\' + pos + \':空位</center>\';\r\n            break;\r\n        case \'-1\':\r\n            x.innerHTML = \'<center>\' + pos + \':已被劃記</center>\';\r\n            break;\r\n        case \'0\':\r\n            x.innerHTML = \'<center>\' + pos + \':被報名中</center>\';\r\n            break;\r\n        default:\r\n            x.innerHTML = \'<center>\' + pos + \'<br>Something wrong!</center>\';\r\n    }\r\n}\r\n\r\n/*當滑鼠離開格子之後，讓顯示恢復原設定*/\r\nfunction set_origin(x, pos){\r\n    x.innerHTML=pos;\r\n}\r\n\r\n\r\n', 'ChoosePage.js', '', 'inherit', 'closed', 'closed', '', '210-revision-v1', '', '', '2020-05-31 14:37:16', '2020-05-31 06:37:16', '', 210, 'http://127.0.0.1/2020/05/31/210-revision-v1/', 0, 'revision', '', 0),
(850, 1, '2020-05-31 14:37:35', '2020-05-31 06:37:35', '/* session已於snippet:php_connection全域啟動，此處毋需再啟用 */// session_start();\r\n\r\n/*使用$_SESSION[\'zone\']來接HTTP以GET傳遞的zone，zone表示本頁面的區塊*/\r\n$_SESSION[\'zone\']=$_GET[\'zone\'];\r\n\r\n/*檢查確定有接收到HTTP GET才連接資料庫做查詢，否則若傳入\'空\'值，資料庫會回傳所有資料*/\r\nif(isset($_SESSION[\'zone\']) && (!empty($_GET[\'zone\'])) ){\r\n    /*call myphp_function: get_positions()回傳位置,pos指position*/\r\n    $_SESSION[\'positions\']=get_position($_SESSION[\'zone\']);\r\n}else{\r\n    /*若沒接到GET執行這裡*/\r\n    /**\r\n     * 可以做重新導向，但這會對編輯網頁的人員會造成麻煩\r\n     * 畢竟網頁流程，使用者正常使用不會發生錯誤，因此這裡僅做錯誤訊息回報\r\n     */\r\n    echo \"本頁面需要搭配網址含有HTTP GET才會正常顯示，若您是網管人員請放心編輯<br>\";\r\n\r\n    unset($_SESSION[\'zone\']);\r\n    mysqli_close($_SESSION[\'con\']);\r\n    unset($_SESSION[\'con\']);\r\n}', 'ChoosePage_Onload.php', '', 'inherit', 'closed', 'closed', '', '205-revision-v1', '', '', '2020-05-31 14:37:35', '2020-05-31 06:37:35', '', 205, 'http://127.0.0.1/2020/05/31/205-revision-v1/', 0, 'revision', '', 0),
